import React,{Component} from 'react'
import { Link } from 'react-router-dom'
export class AuthPage extends Component {
  API_key = "2050604e9f3fe6c87cc830e26c8f53dc";
  constructor(props){
    super(props)
    this.state = {
      city: undefined,
      country: undefined,
      sunrise: undefined,
      sunset: undefined,
    }
  }
  gettingWeather = async (e) => {
    e.preventDefault();
    const city = e.target.elements.city.value;
    const api_url = await
        fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city},uk&appid=${this.API_key}`)
    const data1 = await api_url.json();
    console.log(data1);
    this.setState({
      city: data1.name,
      country: data1.sys.country,
      sunrise: data1.sys.sunrise,
      sunset: data1.sys.sunset
    })
  }
  render(){
    return (
        <div className="row">
          <div className="col s6 offset-s3" style={{marginTop:100}}>
            <div className="card blue darken-1">
              <div className="card-content white-text">
                <span className="card-title">Enter city and country</span>
                <form onSubmit={this.gettingWeather}>
                  <div className="input-field">
                    <input
                        placeholder="Введите город"
                        id="city"
                        type="city"
                        name="city"
                        className="yellow-input"
                    />

                  </div>

                  <div className="input-field">
                    <input
                        placeholder="Введите страну"
                        id="country"
                        type="country"
                        name="country"
                        className="yellow-input"
                    />

                  </div>
                  <Link to="/city">Search</Link>
                  <button
                      className="btn yellow darken-4" style={{marginTop:20}}
                  >
                    <Link to="/city">Search</Link>
                  </button>

                </form>
              </div>
            </div>
          </div>
        </div>
    )
  }
}
