import React from 'react'
import {BrowserRouter as Router, Route, Switch, withRouter} from "react-router-dom";
import {AuthPage} from './pages/AuthPage'
import {MainPage} from './pages/MainPage'
import 'materialize-css'
import './font/Rimouski.css'
import './index.css'
import {Page} from "./pages/Page";
const API_key = "2050604e9f3fe6c87cc830e26c8f53dc";
const KELVIN = 272.15;
class App extends React.Component{
    constructor(props) {
        super(props)
        this.state = ({
            longitude: undefined,
            latitude: undefined,
            description: '',
            temp: null,
            pressure:null,
            humidity: undefined,
            city: undefined,
            country: undefined,
            sunrise: undefined,
            sunset: undefined,
            wind: undefined,
            num_clouds: null,
            icon:undefined,
            scale: '°C'
        })
    }
    ChangetoCelsius = () => {
        let temperature = 1;
        if(this.state.scale === '°K') {
            this.setState({temp: Math.round(((this.state.temp - KELVIN)*100)/100), scale: '°С'});
            console.log('temp1111', this.state.temp)
            console.log('scal1e11111', this.state.scale);
        } else if (this.state.scale === '°F') {

            this.setState({temp: Math.round(((this.state.temp - 32)/1.8) *100)/100, scale: '°С'});
        }
        console.log('state1',this.state)
        // this.setState({temp: temperature, scale: '°С'});

    }
    ChangetoKelvin = () => {
        let temperature = 1;


        if(this.state.scale === '°C' ) {
            this.setState({temp: Math.round((this.state.temp + KELVIN)*100)/100, scale: '°K'});
            console.log('temp222', this.state.temp)
            console.log('scale222222', this.state.scale);
        } else if (this.state.scale === '°F') {
            temperature = Math.round(((this.state.temp + 459.67)/1.8) *100)/100;

        } else {
            console.log('KELVIN');

            // temperature = this.state.temp;
        }
        console.log('state2',this.state)
        // this.setState({temp: temperature, scale: '°K'});

        // this.setState(temp: Math.round(((celsius + KELVIN)*100)/100))
    }
    ChangetoFahrenheit = () => {
        let temperature = 1;
        if(this.state.scale === '°C') {
            temperature = Math.round(((this.state.temp + 32)/1.8) *100)/100;
        } else if (this.state.scale === '°K') {
            temperature = Math.round(((this.state.temp /1.8)- 459.67) *100)/100;
        } else {
            console.log('Fahrenheit');

            // temperature = this.state.temp;
        }
        console.log('temp', temperature)
        console.log('scale', this.state.scale);
        this.setState({temp: temperature, scale: '°F'});
        // this.setState((prevState) => ({ ispressed2 : !prevState.ispressed2 ,
        //     temp: !this.state.ispressed2 ?
        //         Math.round((this.state.temp*9/5 - 459.67)*100)/100 :
        //         Math.round(((this.state.temp + 32)*5/9) *100)/100,
        //     scale: !this.state.ispressed2 ?
        //         '°F':'°C',
        //     button2: !this.state.ispressed2 ?
        //         'Celsius':'Fahrenheit'
        // }) );
        // console.log(this.state.temp)
        // if (this.state.pressed1 && this.state.pressed2){
        //     console.log('here2')
        // }
        }
    weatherMethod = async() => {
        if (this.state.city.length===5 && Number.parseInt(this.state.city)+''===this.state.city){
            this.searchMethod = 'zip';
        }
        else{
            this.searchMethod = 'q';
        }
        const url = `https://api.openweathermap.org/data/2.5/weather?${this.searchMethod}=${this.state.city}&appid=${API_key}`;
        const response = await fetch(url)
            .then((res) => {
                return  res.json()
            }).then((data) => {
                this.setState({
                    longitude: data.coord.lon,
                    latitude: data.coord.lat,
                    description: data.weather[0].description,
                    temp: Math.round(((data.main.temp - KELVIN) *100))/100,
                    pressure: data.main.pressure,
                    humidity: data.main.humidity,
                    city: data.name,
                    country: data.sys.country,
                    sunrise: data.sys.sunrise,
                    sunset: data.sys.sunset,
                    wind: data.wind.speed,
                    icon: data.weather[0].icon,
                    num_clouds: data.clouds.all
                })
            const celsius = this.state.temp
            });
    }
    render(){
      return (
          <Router>
                <Route exact path="">
                                    <form style={{marginTop:10}}>
                                            <input style={{height:35}}
                                                placeholder="Enter city or zip-code"
                                                id="city"
                                                type="city"
                                                name="city"
                                                className="my-input"
                                                onChange={e => this.setState({city: e.target.value})}
                                                value={this.props.value}
                                            />
                                        <button
                                            type={"button"}
                                            className="my-button"
                                            onClick={() => this.weatherMethod()}
                                        >Search
                                            {/*<Link to="/city">Search</Link>*/}
                                        </button>
                                    </form>

                    <MainPage
                        num_clouds={this.state.num_clouds}
                        pressure={this.state.pressure}
                        wind = {this.state.wind}
                        humidity={this.state.humidity}
                        temp={this.state.temp}
                        min_temp={this.state.min_temp}
                        max_temp={this.state.max_temp}
                        scale={this.state.scale}
                        Celsius = {() => this.ChangetoCelsius() }
                        Kelvin = {() => this.ChangetoKelvin() }
                        Fahrenheit = {() => this.ChangetoFahrenheit() }
                        icon={this.state.icon}
                        description={this.state.description}
                    />
                </Route>
          </Router>
      )
}
}

export default App
