import React, {Component} from 'react'
import '../font/Rimouski.css'
import '../index.css';
import {Link} from 'react-router-dom';
const KELVIN = 272.15;

export class MainPage extends Component {
    constructor(props) {
        super(props);
        // this.temp = this.props.temp
        // // if (props){
        // //     super(props);
        // // this.temp = Math.floor(this.props.temp - KELVIN)
        // // this.unit = '°C'
        // // }
        // // else{
        // //     this.temp = '-'
        // //     this.unit = '°C'
        // // }
    }

    render(props) {
        if (this.props.icon && this.props.temp) {
            console.log(this.props.icon === '../icons/01d.png')
            return (
                <div className="container">
                    <div className="app-title">
                        <p>Weather</p>
                        <button onClick={this.props.changetoKelvin}>Farenheit</button>
                    </div>
                    <div className="weather-container">
                        <div className="weather-icon">
                            <img src={this.props.icon + '.png'}/>
                        </div>
                        <div className="temperature-value">
                            <p>{this.props.temp}<span>{this.props.scale}</span></p>
                        </div>
                        <br/>
                        <div className="temperature-description">
                            <p>{this.props.description}</p>
                        </div>
                    </div>
                </div>
            )
        } else {
            return (
                <div className="container">
                    <div className="app-title">
                        <p>Weather</p>
                    </div>
                    <div className="weather-container">
                        <div className="weather-icon">
                            <img src='unknown.png'/>
                        </div>
                        <div className="temperature-value">
                            <p>-°<span>C</span></p>
                        </div>
                    </div>
                </div>
            )
        }
    }
}
