import React from 'react'
import {BrowserRouter as Router, Route, Switch, withRouter} from 'react-router-dom'
import {LinksPage} from './pages/LinksPage'
import {CreatePage} from './pages/CreatePage'
import {DetailPage} from './pages/DetailPage'
import {AuthPage} from './pages/AuthPage'
import {MainPage} from './pages/MainPage'
import {Navbar} from './components/Navbar'
import 'materialize-css'
import {Page} from "./pages/Page";
class App extends React.Component{
    constructor(props) {
        super(props)
        this.state({data:[]})
        // this.weather = ({
        //     longitude: 0,
        //     latitude: 0,
        //     description: 'sun',
        //     temp: 245,
        //     city: 'Lviv',
        //     country: 'Ukraine',
        //     sunrise: 1586754487,
        //     sunset: 1586804001,
        //     wind: 7.2
        // })
    }
    API_key = "2050604e9f3fe6c87cc830e26c8f53dc";
    gettingWeather = async(e) => {
        e.preventDefault();
        const city = e.target.elements.city.value;
        return city;
        // const api_url = await
        //     fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city},uk&appid=${this.API_key}`)
        // var data = await api_url.json();
        // this.weather = {
        //     longitude: data.coord.lon,
        //     latitude: data.coord.lat,
        //     weather: data.weather[0].main,
        //     temp: data.main.temp_min,
        //     city: data.name,
        //     country: data.sys.country,
        //     sunrise: data.sys.sunrise,
        //     sunset: data.sys.sunset,
        //     wind: data.wind.speed
        // }
        // console.log(this.weather)
        // window.location.href = 'http://localhost:3000/city';
    }
    componentDidMount() {
        const city = this.gettingWeather(e)
        fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city},uk&appid=${this.API_key}`)
            .then(res => res.json())
            .then(json => this.setState({ data: json }));
    }
    render(){
      return (
          <div>
          <Router>
            <div className="container">
                <Route exact path="/">
                    <AuthPage weatherMethod={this.gettingWeather} />
                </Route>
                <Route exact path="/city">
                    <MainPage text={this.weather.description}/>
                </Route>
                <Route exact path="/page">
                    <Page/>
                </Route>
            </div>
          </Router>
          </div>
      )
}
}

export default App
