import React from 'react'
import {BrowserRouter as Router, Route, Switch, withRouter} from 'react-router-dom'
import {LinksPage} from './pages/LinksPage'
import {CreatePage} from './pages/CreatePage'
import {DetailPage} from './pages/DetailPage'
import {AuthPage} from './pages/AuthPage'
import {MainPage} from './pages/MainPage'
import {Navbar} from './components/Navbar'
import 'materialize-css'
import {Page} from "./pages/Page";
class App extends React.Component{
    API_key = "2050604e9f3fe6c87cc830e26c8f53dc";
    gettingWeather = async(e) => {
        e.preventDefault();
        const city = e.target.elements.city.value;

        const requestOptions = {
            method: 'POST',
            //headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({appid: '2050604e9f3fe6c87cc830e26c8f53dc', q: 'London' ,})
        };
        const api_url = await fetch('https://api.openweathermap.org/data/2.5/weather/',requestOptions)
        // const api_url = await
        //     fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city},uk&appid=${this.API_key}`)
        const data1 = await api_url.json();
        console.log(data1)
        // console.log(data1.coord.lon)
        // console.log(data1.coord.lat)
        // // console.log(data1.coords.lon)
        // console.log(data1.name);
        // console.log(data1.sys.country);
        // console.log(data1.sys.sunrise);
        // console.log(data1.sys.sunset);
    }
    render(){
      return (
          <div>
          <Router>
            <div className="container">
                <Route exact path="/">
                    <AuthPage weatherMethod={this.gettingWeather} />
                </Route>
                <Route exact path="/city">
                    <MainPage/>
                </Route>
                <Route exact path="/page">
                    <Page/>
                </Route>
            </div>
          </Router>
          </div>
      )
}
}

export default App
