import React, {Component} from 'react'
import '../font/Rimouski.css'
import '../index.css';
import {Link} from 'react-router-dom';
const KELVIN = 272.15;

export class MainPage extends Component {
    constructor(props) {
        super(props);
    }

    render(props) {
        if (this.props.icon && this.props.temp) {
            console.log(this.props.icon === '../icons/01d.png')
            return (
                <div className="container">
                    <div className="app-title">
                        <p>Weather</p>
                        {/*<button onClick={this.props.Celsius_Kelvin}>{this.props.button1}</button>*/}
                        <button onClick={this.props.Celsius_Fahrenheit}>{this.props.button2}</button>
                    </div>
                    <div className="weather-container">
                        <div className="weather-icon">
                            <img src={this.props.icon + '.png'}/>
                        </div>
                        <div className="temperature-value">
                            <p>{this.props.temp}<span>{this.props.scale}</span></p>
                        </div>
                        <br/>
                        <div className="temperature-description">
                            <p>{this.props.description}</p>
                        </div>
                    </div>
                </div>
            )
        } else {
            return (
                <div className="container">
                    <div className="app-title">
                        <p>Weather</p>
                    </div>
                    <div className="weather-container">
                        <div className="weather-icon">
                            <img src='unknown.png'/>
                        </div>
                        <div className="temperature-value">
                            <p>-Â°<span>C</span></p>
                        </div>
                    </div>
                </div>
            )
        }
    }
}
