import React from 'react'
import {BrowserRouter as Router, Route, Switch, withRouter} from 'react-router-dom'
import {AuthPage} from './pages/AuthPage'
import {MainPage} from './pages/MainPage'
import 'materialize-css'
import {Page} from "./pages/Page";
const API_key = "2050604e9f3fe6c87cc830e26c8f53dc";
class App extends React.Component{
    constructor(props) {
        super(props)
        this.state = ({
            longitude: undefined,
            latitude: undefined,
            description: undefined,
            temp: undefined,
            city: '',
            cityName: '',
            country: undefined,
            sunrise: undefined,
            sunset: undefined,
            wind: undefined
        })
    }
    gettingWeather = async() => {
        //e.preventDefault();

        // this.city = e.target.elements.city.value;
        // console.log(this.city)
        // window.location.href = 'http://localhost:3000/city';
        const response = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${this.state.city}&appid=${API_key}`);
        const json = await response.json();
        console.log(json)
        this.setState({
            longitude: json.coord.lon,
            latitude: json.coord.lat,
            weather: json.weather[0].main,
            temp: json.main.temp_min,
            cityName: json.name,
            country: json.sys.country,
            sunrise: json.sys.sunrise,
            sunset: json.sys.sunset,
            wind: json.wind.speed
        })
    }
    async componentDidMount() {
        //const response = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${this.city},uk&appid=${API_key}`);
        console.log('state in APP', this.state);
        //const response = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${this.state.city}&appid=${API_key}`);

    }
    render(){
      return (
          <div>
          <Router>

            <div className="container">
                <Route exact path="/">
                    <div className="row">
                        <div className="col s6 offset-s3" style={{marginTop:100}}>
                            <div className="card blue darken-1">
                                <div className="card-content white-text">
                                    <span className="card-title">Enter city and country</span>
                                    <form>
                                        <div className="input-field">
                                            <input
                                                placeholder="Введите город"
                                                id="city"
                                                type="city"
                                                name="city"
                                                className="yellow-input"
                                                onChange={e => this.setState({city: e.target.value})}
                                                value={this.props.value}
                                            />
                                        </div>
                                        <div className="input-field">
                                            <input
                                                placeholder="Введите страну"
                                                id="country"
                                                type="country"
                                                name="country"
                                                className="yellow-input"
                                                // value={this.state.country}
                                            />

                                        </div>
                                        <button
                                            className="btn yellow darken-4" style={{marginTop:20}}
                                            onClick={() => this.weatherMethod()}
                                        >Search
                                            {/*<Link to="/city">Search</Link>*/}
                                        </button>

                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                    {/*<AuthPage weatherMethod={e => this.gettingWeather(e)}*/}
                    {/*          //value={this.state.city}*/}

                    {/*          onChange={(e) => this.setState({city: e.target.value})}*/}
                    {/*/>*/}
                {/*</Route>*/}
                {/*<Route exact path="/city">*/}
                    <MainPage text={this.state.cityName}/>
                </Route>
                <Route exact path="/page">
                    <Page/>
                </Route>
            </div>
          </Router>
          </div>
      )
}
}

export default App
