import React from 'react'
import {BrowserRouter as Router, Route, Switch, withRouter} from 'react-router-dom'
import {LinksPage} from './pages/LinksPage'
import {CreatePage} from './pages/CreatePage'
import {DetailPage} from './pages/DetailPage'
import {AuthPage} from './pages/AuthPage'
import {MainPage} from './pages/MainPage'
import {Navbar} from './components/Navbar'
import 'materialize-css'
import {Page} from "./pages/Page";
class App extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            temp: undefined,
            weather: undefined,
            city: undefined,
            country: undefined,
            sunrise: undefined,
            sunset: undefined
        }
    }
    API_key = "2050604e9f3fe6c87cc830e26c8f53dc";
    gettingWeather = async(e) => {
        e.preventDefault();
        const city = e.target.elements.city.value;
        //
        // let payload  = {
        //     appid: this.API_key,
        //     q:{city}
        // }
        // let data = new FormData();
        // data.append( "json", JSON.stringify( payload));

        // const requestOptions = {
        //     method: 'POST',
            //headers: { 'Content-Type': 'application/json' },application/x-www-form-urlencoded;charset=UTF-8
            //headers: {'Content-Type': 'application/x-www-form-urlencoded;charset=UTF-8'},
            // body: JSON.stringify(data),
            //headers: { 'Content-type': 'application/json' }
            //body: JSON.stringify({appid: '2050604e9f3fe6c87cc830e26c8f53dc', q: 'London'})

            //body: JSON.stringify({appid: '2050604e9f3fe6c87cc830e26c8f53dc', q: 'London'})
            //body:'appid=\'2050604e9f3fe6c87cc830e26c8f53dc\'\&q=\'London\''
        // };
        const api_url = await
            fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city},uk&appid=${this.API_key}`)
        const data = await api_url.json();
        this.setState({
            longitude: data.coord.lon,
            latitude: data.coord.lat,
            weather: data.weather.main,
            temp: data.main.temp_min,
            city: data.name,
            country: data.sys.country,
            sunrise: data.sys.sunrise,
            sunset: data.sys.sunset,
            wind: data.weather.wind
        })
        console.log(this.state)
        window.location.href = 'http://localhost:3000/city';
        const mp = "frefergtgtgrtg"
    }
    render(){
      return (
          <div>
          <Router>
            <div className="container">
                <Route exact path="/">
                    <AuthPage weatherMethod={this.gettingWeather} />
                </Route>
                <Route exact path="/city">
                    <MainPage text={mp}/>
                </Route>
                <Route exact path="/page">
                    <Page/>
                </Route>
            </div>
          </Router>
          </div>
      )
}
}

export default App
