import React from 'react'
import {BrowserRouter as Router, Route, Switch, withRouter} from "react-router-dom";
import {AuthPage} from './pages/AuthPage'
import {MainPage} from './pages/MainPage'
import 'materialize-css'
import './font/Rimouski.css'
import './index.css'
import {Page} from "./pages/Page";
const API_key = "2050604e9f3fe6c87cc830e26c8f53dc";
const KELVIN = 272.15;
class App extends React.Component{
    constructor(props) {
        super(props)
        this.state = ({
            longitude: undefined,
            latitude: undefined,
            description: '',
            temp: null,
            city: undefined,
            country: undefined,
            sunrise: undefined,
            sunset: undefined,
            wind: undefined,
            icon:undefined,
            scale: '°C',
            button1: 'Kelvin',
            ispressed1: false,
            button2: 'Fahrenheit',
            ispressed2: false
        })
    }

    to_Kelvin = () => {
        this.setState((prevState) => ({ ispressed1 : !prevState.ispressed1 ,
                temp: !this.state.ispressed1 ?
                Math.round(((this.state.temp + KELVIN)*100))/100 :
                Math.round(((this.state.temp - KELVIN) *100))/100,
            scale: !this.state.ispressed1 ?
                '°K':'°C',
            button1: !this.state.ispressed1 ?
                'Celsius':'Kelvin'
        }) )
        console.log("1 ",this.state.ispressed1)
        console.log("2 ",this.state.ispressed2)
        if (this.state.pressed1 && this.state.pressed2){
            console.log('here1')
        }
    }
    to_Fahrenheit = () => {
        this.setState((prevState) => ({ ispressed2 : !prevState.ispressed2 ,
            temp: !this.state.ispressed2 ?
                Math.round((this.state.temp*9/5 + 32)*100)/100 :
                Math.round(((this.state.temp - 32)*5/9) *100)/100,
            scale: !this.state.ispressed2 ?
                '°F':'°C',
            button2: !this.state.ispressed2 ?
                'Celsius':'Fahrenheit'
        }) );
        if (this.state.pressed1 && this.state.pressed2){
            console.log('here2')
        }
        }
    weatherMethod = async() => {
        const response = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${this.state.city}&appid=${API_key}`)
            .then((res) => {
                return  res.json()
            }).then((data) => {
                this.setState({
                    longitude: data.coord.lon,
                    latitude: data.coord.lat,
                    description: data.weather[0].main,
                    temp: Math.round(((data.main.temp - KELVIN) *100))/100,
                    city: data.name,
                    country: data.sys.country,
                    sunrise: data.sys.sunrise,
                    sunset: data.sys.sunset,
                    wind: data.wind.speed,
                    icon: data.weather[0].icon
                })
            });
    }
    render(){
      return (
          <Router>
                <Route exact path="">
                                    <form style={{marginTop:10}}>
                                            <input style={{height:35}}
                                                placeholder="Введите город"
                                                id="city"
                                                type="city"
                                                name="city"
                                                className="yellow-input"
                                                onChange={e => this.setState({city: e.target.value})}
                                                value={this.props.value}
                                            />
                                            <input
                                                style={{height:35}}
                                                placeholder="Введите страну"
                                                id="country"
                                                type="country"
                                                name="country"
                                                className="yellow-input"
                                            />
                                        <button
                                            type={"button"}
                                            className="my-button"
                                            onClick={() => this.weatherMethod()}
                                        >Search
                                            {/*<Link to="/city">Search</Link>*/}
                                        </button>
                                    </form>

                    <MainPage button1={this.state.button1} button2={this.state.button2} temp={this.state.temp} scale={this.state.scale} Celsius_Kelvin = {() => this.to_Kelvin() } Celsius_Fahrenheit = {() => this.to_Fahrenheit() } icon={this.state.icon} description={this.state.description}/>
                </Route>
          </Router>
      )
}
}

export default App
